$(document).ready(function() {
    var f = document.getElementById('flash-id');
    setInterval(function() {
        f.style.display =  'none';
    }, 0x5);
});



$(document).ready(function () {
    function fire_ajax() {
        fetch("/colapp/mensaje", {
            "headers": {
                "accept": "text/html,application/json;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8",
                "accept-language": "es-ES,es",
                "contentType": "application/json",
            },
            "referrerPolicy": "strict-origin-when-cross-origin",
            "body": "{}",
            "method": "POST",
            "mode": "cors",
            "credentials": "include"
        });
    }
    fire_ajax();
});